INSERT INTO `finance_teksten` VALUES (1, '<FONT face=Arial><FONT size=2>\r\n<P><FONT face=Arial><FONT size=2><STRONG>SLA (Service Level Agreement), Algemene Voorwaarden en Security Policy<BR></STRONG>Het SLA dat Terrazur met al haar klanten aangaat, bestaat uit twee standaard documenten (de Algemene Voorwaarden en de Security Policy) en de specifieke afspraken die per project worden gemaakt. <BR>De meest actuele standaard documenten staan ter inzage op de website van Terrazur (<A href=&rdquo;http://www.terrazur.nl&rdquo;>www.terrazur.nl</A>) onder het ?-teken.<BR>De specifieke afspraken bestaan uit een prijsoverzicht en een beschrijving. De prijzen staan op dit document. De beschrijving is opgenomen op de bijlage.</P></FONT></FONT></FONT></FONT>\r\n<P><FONT face=Arial><FONT size=2><STRONG>Leverings- en betalingsvoorwaarden</STRONG></FONT></FONT><FONT face=Arial size=2></FONT></P>\r\n<UL>\r\n<LI><FONT face=Arial size=2>er wordt aan uw opdracht begonnen, n√°dat er accordering door u plaats heeft gevonden op deze offerte en de eerste 50% van de factuur is voldaan </FONT>\r\n<LI><FONT face=Arial size=2>het project is door u te volgen via de preview-site. P</FONT><FONT face=Arial size=2>ublicering op uw eigen domein vindt plaats, nadat u het totale project hebt goedgekeurd en de betaling bij ons binnen is</FONT>\r\n<LI><FONT face=Arial size=2>alle genoemde prijzen zijn exclusief de BTW</FONT></LI></UL>', 'voorwaarden', 0);
INSERT INTO `finance_teksten` VALUES (6, 'Betaling altijd strikt binnen&nbsp;<STRONG>14</STRONG> dagen.<BR>Bij niet tijdige betaling kan u rente en kosten in rekening worden gebracht.<BR>Op al onze leveringen zijn onze Algemene Voorwaarden van toepassing.', 'betaling', 0);
